sp_widget.client_script:
function($scope) {
	/* widget controller */
	var c = this;
	c.dataJson = $scope.data.dataJson;
	c.noData='notLoaded';
	c.searchStr = '';
	c.init = function() {
		  // init for these samples -- you don't need to call this
			var $ = go.GraphObject.make;  // for conciseness in defining templates

		if(typeof myDiagram !== 'undefined')
			myDiagram.div = null;
		myDiagram =
		$(go.Diagram, "myDiagramDiv",  // must name or refer to the DIV HTML element
		{
			// start everything in the middle of the viewport
			initialContentAlignment: go.Spot.Center,
			// have mouse wheel events zoom in and out instead of scroll up and down
			"toolManager.mouseWheelBehavior": go.ToolManager.WheelZoom,
			// enable undo & redo
			"undoManager.isEnabled": true
		});

		// define the Node template
		myDiagram.nodeTemplate =
		$(go.Node, "Auto",
		new go.Binding("location", "loc", go.Point.parse).makeTwoWay(go.Point.stringify),
		// define the node's outer shape, which will surround the TextBlock
		$(go.Shape, "RoundedRectangle",
		{
			parameter1: 20,  // the corner has a large radius
			fill: $(go.Brush, "Linear", { 0: "rgb(254, 201, 0)", 1: "rgb(254, 162, 0)" }),
				stroke: null,
				portId: "",  // this Shape is the Node's port, not the whole Node
				fromLinkable: true, fromLinkableSelfNode: true, fromLinkableDuplicates: true,
				toLinkable: true, toLinkableSelfNode: true, toLinkableDuplicates: true,
				cursor: "pointer",
				width:250
			}),
			$(go.TextBlock,
			{
				font: "bold 11pt helvetica, bold arial, sans-serif",
				editable: true,  // editing the text automatically updates the model data
				width:200
			},
			new go.Binding("text").makeTwoWay())
			);
			// unlike the normal selection Adornment, this one includes a Button
			myDiagram.nodeTemplate.selectionAdornmentTemplate =
			$(go.Adornment, "Spot",
			$(go.Panel, "Auto",
			$(go.Shape, { fill: null, stroke: "blue", strokeWidth: 2 }),
				$(go.Placeholder)  // a Placeholder sizes itself to the selected Node
				) // end button
				); // end Adornment

				// replace the default Link template in the linkTemplateMap
				myDiagram.linkTemplate =
				$(go.Link,  // the whole link panel
				{
					curve: go.Link.Bezier, adjusting: go.Link.Stretch,
					reshapable: true, relinkableFrom: true, relinkableTo: true,
					toShortLength: 3
				},
				new go.Binding("points").makeTwoWay(),
				new go.Binding("curviness"),
				$(go.Shape,  // the link shape
				{ strokeWidth: 1.5 }),
					$(go.Shape,  // the arrowhead
					{ toArrow: "standard", stroke: null })

						);
		console.log(c.dataJson);
		if(c.dataJson){
			myDiagram.model = go.Model.fromJson(c.dataJson);
		}



					};

					c.search = function(e){

						if (e && e.keyCode != 13) {
							return;
						}
						console.log(c.searchStr);
						if(c.searchStr==''){
							alert('enter a script include and method name to search');
							return;
						}
						var input ={'searchStr':c.searchStr};
							c.server.get(input).then(function(r){
								c.dataJson = r.data.dataJson;
								console.log(JSON.stringify(c.dataJson));
								if(c.dataJson){
									c.noData = 'loaded';
								}else{
									c.noData = 'noData';
								}
								console.log(c.noData);
								c.init();

							});

						};
						//c.init();

					}

sp_widget.css:
.center {
	text-align:center;
  color: gray;
}
.btn {
    background-color: #f8f8f8;
}
.left {
	text-align: left;
}

